}

.wp-block-navigation:not(.has-background) .wp-block-navigation__submenu-container {  
background-color: #fff;
    color: black;
    border-radius: 15px;
padding-top:10px;
padding-bottom:10px;
padding-left:10px;
padding-right:10px;
}
.wp-block-navigation:not(.has-background) .wp-block-navigation__submenu-container {
    background-color: #transparent!important;
    border: 1px solid #ddd;
    padding: 8px 0;

}
/* Estilo base para o container do submenu */
.wp-block-navigation:not(.has-background) .wp-block-navigation__submenu-container {
    background-color: #fff;
    border: 1px solid #ddd;
    padding: 8px 0;

}

/* Ajustes para o menu responsivo */
.wp-block_navigation_responsive-container.is-menu-open .wp-block-navigation_responsive-container-content .wp_block-navigation_container {
    background-color: transparent !important;
}

.wp-block_navigation_responsive-container.is-menu-open .wp-block-navigation_responsive-container-content .wp_block-navigation_container a {
    color: #000 !important;
}

/* Ajustes para dispositivos móveis */
@media screen and (max-width: 768px) {
    .wp-block-navigation:not(.has-background) .wp-block-navigation__submenu-container {
        position: relative !important;

        left: 0 !important;
        top: 0 !important;
        box-shadow: none;
        border: none;
        background-color: transparent;
        margin-left: 1rem;
    }

    .wp-block-navigation__submenu-container .wp-block-navigation-item {
        padding: 8px 16px;

    }

    .wp-block-navigation__responsive-container.is-menu-open .wp-block-navigation__submenu-container {
        padding-top: 8px;
        padding-bottom: 8px;
        display: block;
        background-color: transparent;
    }
}
/* Ajustes para dispositivos móveis */
@media screen and (max-width: 768px) {
    .wp-block-navigation:not(.has-background) .wp-block-navigation__submenu-container {
        position: relative !important;
        width: 100% !important;
        left: 0 !important;
        top: 0 !important;
        box-shadow: none;
        border: none;
        background-color: #f5f5f5;
        margin-left: 1rem;
    }

    .wp-block-navigation__submenu-container .wp-block-navigation-item {
        padding: 8px 16px;

    }

    .wp-block-navigation__responsive-container.is-menu-open .wp-block-navigation__submenu-container {
        padding-top: 8px;
        padding-bottom: 8px;
        display: block;
    }
}
